mapper.select:
  		select 

  	      from t_sys_user where 1=1 
		  	and id = #{ vo.id }
  	        and user_name = #{ vo.userName }
  	        and account = #{ vo.account }
  	        and email = #{ vo.email }
  	        and mobile_phone = #{ vo.mobilePhone }
  	        and password = #{ vo.password }
		  	and reg_time = #{ vo.regTime }
  	        and reg_ip = #{ vo.regIp }
		  	and last_login_time = #{ vo.lastLoginTime }
		  	and last_login_err_time = #{ vo.lastLoginErrTime }
  	        and last_login_ip = #{ vo.lastLoginIp }
  	        and id_number = #{ vo.idNumber }
		  	and login_err_times = #{ vo.loginErrTimes }
		  	and user_type = #{ vo.userType }
		  	and status = #{ vo.status }

select 
from t_sys_user where 1=1

	AND  id = #{id,jdbcType=INTEGER}


	AND  user_name = #{userName,jdbcType=VARCHAR}


	AND  account = #{account,jdbcType=VARCHAR}


	AND  email = #{email,jdbcType=VARCHAR}


	AND  mobile_phone = #{mobilePhone,jdbcType=VARCHAR}


	AND  password = #{password,jdbcType=VARCHAR}


	AND  reg_time = #{regTime,jdbcType=TIMESTAMP}


	AND  reg_ip = #{regIp,jdbcType=VARCHAR}


	AND  last_login_time = #{lastLoginTime,jdbcType=TIMESTAMP}


	AND  last_login_err_time = #{lastLoginErrTime,jdbcType=TIMESTAMP}


	AND  last_login_ip = #{lastLoginIp,jdbcType=VARCHAR}


	AND  id_number = #{idNumber,jdbcType=VARCHAR}


	AND  login_err_times = #{loginErrTimes,jdbcType=INTEGER}


	AND  user_type = #{userType,jdbcType=INTEGER}


	AND  status = #{status,jdbcType=INTEGER}

mapper.delete:
	   delete from t_sys_user
	   where id in

              #{id,jdbcType=INTEGER}

mapper.select:
SELECT * FROM t_sys_user WHERE account = #{account,jdbcType=VARCHAR}  
UNION ALL  
SELECT * FROM t_sys_user WHERE  email = #{account,jdbcType=VARCHAR} 
UNION ALL  
SELECT * FROM t_sys_user WHERE  mobile_phone = #{account,jdbcType=VARCHAR} 
UNION ALL  
SELECT * FROM t_sys_user WHERE  id_number = #{account,jdbcType=VARCHAR}

select count(*) total from t_sys_user where 1=1

	and account=#{account,jdbcType=VARCHAR}


	and email=#{email,jdbcType=VARCHAR}


	and mobile_phone=#{mobilePhone,jdbcType=VARCHAR}


	and id_number=#{idNumber,jdbcType=VARCHAR}
